import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import SecureContent from "../contexts/secure-context.js";
import * as memeService from "../services/meme-service.js";
import * as userService from "../services/user-service.js";
import * as authService from "../services/auth-service.js";
import { useProfile } from "../contexts/profile-context.js";

const MemeDetails = () => {
  const [meme, setMeme] = useState({});
  const [ourMeme, setOurMeme] = useState({});
  const [liked, setLiked] = useState();
  const [disliked, setDisliked] = useState();
  const { memeID } = useParams();
  const { checkLoggedIn } = useProfile();

  const handleUserActions = async (action) => {
    const curSessionUser = await checkLoggedIn();
    const curUser = await userService.fetchUserById(curSessionUser._id);
    let updatedcurUser;
    switch (action) {
      case "like":
        await userService.likeMeme(curUser.data, memeID);
        break;
      case "unlike":
        await userService.unlikeMeme(curUser.data, memeID);
        break;
      case "dislike":
        await userService.dislikeMeme(curUser.data, memeID);
        break;
      case "undislike":
        await userService.undislikeMeme(curUser.data, memeID);
        break;
      case "unlikeDislike":
        await userService.unlikeMeme(curUser.data, memeID);
        updatedcurUser = await userService.fetchUserById(curSessionUser._id);
        await userService.dislikeMeme(updatedcurUser.data, memeID);
        break;
      case "undislikeLike":
        await userService.undislikeMeme(curUser.data, memeID);
        updatedcurUser = await userService.fetchUserById(curSessionUser._id);
        await userService.likeMeme(updatedcurUser.data, memeID);
        break;
      default:
        return;
    }
  };

  const fetchMemeByID = async () => {
    const response = await memeService.fetchMemesFromAPI();
    setMeme(response.data.data.memes.find((curMeme) => curMeme.id === memeID));
  };

  const fetchMemeByIDfromDatabase = async () => {
    const response = await memeService.fetchMemeById(memeID);
    setOurMeme(response.data);
  };

  const checkLiked = async () => {
    const curSessionUser = await checkLoggedIn();
    const curUser = await userService.fetchUserById(curSessionUser._id);
    const curLiked = curUser.data.likedMemes.includes(memeID);
    setLiked(curLiked);
    const curDisliked = curUser.data.dislikedMemes.includes(memeID);
    setDisliked(curDisliked);
  };

  const handleMemeLikes = async () => {
    const curMeme = {
      name: meme.name,
      memeID: meme.id,
      url: meme.url,
    };
    const response = await memeService.likeMeme(curMeme);
    setOurMeme(response.data);
  };

  const handleUserLikes = async () => {
    const curSessionUser = await checkLoggedIn();
    const curUser = await userService.fetchUserById(curSessionUser._id);
    await userService.likeMeme(curUser.data, memeID);
  };

  const handleMemeUnlikes = async () => {
    const curMeme = {
      title: meme.title,
      memeID: meme.id,
      url: meme.url,
    };
    const response = await memeService.unlikeMeme(curMeme);
    setOurMeme(response.data);
  };

  const handleUserUnlikes = async () => {
    const curSessionUser = await checkLoggedIn();
    const curUser = await userService.fetchUserById(curSessionUser._id);
    await userService.unlikeMeme(curUser.data, memeID);
  };

  const handleMemeUnlikeDislikes = async () => {
    const curMeme = {
      name: meme.name,
      memeID: meme.id,
      url: meme.url,
    };
    const response = await memeService.unlikeDislikeMeme(curMeme);
    setOurMeme(response.data);
  };

  const handleUserDislikes = async () => {
    const curSessionUser = await checkLoggedIn();
    const curUser = await userService.fetchUserById(curSessionUser._id);
    await userService.dislikeMeme(curUser.data, memeID);
  };

  const handleMemeUndislikeLikess = async () => {
    const curMeme = {
      title: meme.title,
      memeID: meme.id,
      url: meme.url,
    };
    const response = await memeService.unDislikeLikeMeme(curMeme);
    setOurMeme(response.data);
  };

  const handleUserUndislikes = async () => {
    const curSessionUser = await checkLoggedIn();
    const curUser = await userService.fetchUserById(curSessionUser._id);
    await userService.undislikeMeme(curUser.data, memeID);
  };
  const handleUserUnLikeDislikes = async () => {
    const curSessionUser = await checkLoggedIn();
    const curUser = await userService.fetchUserById(curSessionUser._id);
    await userService.unlikeMeme(curUser.data, memeID);
    const updatedcurUser = await userService.fetchUserById(curSessionUser._id);
    await userService.dislikeMeme(updatedcurUser.data, memeID);
  };
  const handleUserUndislikeLikes = async () => {
    const curSessionUser = await checkLoggedIn();
    const curUser = await userService.fetchUserById(curSessionUser._id);
    await userService.undislikeMeme(curUser.data, memeID);
    const updatedcurUser = await userService.fetchUserById(curSessionUser._id);
    await userService.likeMeme(updatedcurUser.data, memeID);
  };

  useEffect(() => {
    fetchMemeByID();
    fetchMemeByIDfromDatabase();
    checkLiked();
  }, []);

  return (
    <div>
      <h1>{meme.name}</h1>
      <img src={meme.url} height={500} />
      <SecureContent
        loggedInContent={
          <div>
            {" "}
            <i
              className={`${
                liked ? "fas fa-thumbs-up text-danger" : "fas fa-thumbs-up"
              }`}
              onClick={() => {
                if (liked) {
                  handleMemeUnlikes();
                  // handleUserUnlikes();
                  handleUserActions("unlike");
                  setLiked(false);
                } else if (!liked && disliked) {
                  handleMemeUndislikeLikess();
                  // handleUserUndislikeLikes();
                  handleUserActions("undislikeLike");
                  setLiked(true);
                  setDisliked(false);
                } else {
                  handleMemeLikes();
                  // handleUserLikes();
                  handleUserActions("like");
                  setLiked(true);
                }
              }}
            ></i>
            {(ourMeme && ourMeme.likes) || 0}
            <i
              className={`${
                disliked
                  ? "fas fa-thumbs-down text-primary"
                  : "fas fa-thumbs-down"
              }`}
              onClick={() => {
                if (disliked) {
                  handleMemeLikes();
                  // handleUserUndislikes();
                  handleUserActions("undislike");
                  setDisliked(false);
                } else if (liked && !disliked) {
                  handleMemeUnlikeDislikes();
                  // handleUserUnLikeDislikes();
                  handleUserActions("unlikeDislike");
                  setDisliked(true);
                  setLiked(false);
                } else {
                  handleMemeUnlikes();
                  // handleUserDislikes();
                  handleUserActions("dislike");
                  setDisliked(true);
                }
              }}
            ></i>
          </div>
        }
        nonloggedInContent={<div>please login to like or comment</div>}
      />
    </div>
  );
};

export default MemeDetails;
