import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
import { Link, useLocation, useNavigate, useParams } from "react-router-dom";

const SearchOmdb = () => {
  const [memes, setMemes] = useState([]);
  const { searchString } = useParams();
  const titleRef = useRef();
  const navigate = useNavigate();
  const location = useLocation();
  const meme_URL = "https://api.imgflip.com/get_memes";
  const fetchMemes = async () => {
    const response = await axios.get(`${meme_URL}`);
    setMemes(response.data.data.memes);
    console.log(memes);
    // navigate(`/omdb/${titleRef.current.value}`);
  };

  const filterMemesByTitle = (title) => {
    return memes.filter((meme) => meme.name.includes(title));
  };
  useEffect(() => {
    if (searchString) {
      titleRef.current.value = searchString;
      fetchMemes();
    }
  }, []);
  return (
    <div>
      <h1>Search Omdb</h1>

      <ul className="list-group">
        <li className="list-group-item">
          <button
            onClick={searchMoviesByTitle}
            className="btn btn-primary float-end"
          >
            Search
          </button>
          <input ref={titleRef} className="form-control w-75" />
        </li>
        {memes.map((meme) => (
          <li className="list-group-item">
            {/* <Link to={`/omdb/details/${movie.imdbID}`}> */}
            <img src={meme.url} height={30} className="me-2" />
            {meme.name}
            {/* </Link> */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SearchOmdb;
