import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import SecureContent from "../contexts/secure-context.js";
import * as memeService from "../services/meme-service.js";
import * as userService from "../services/user-service.js";
import * as authService from "../services/auth-service.js";
import { useProfile } from "../contexts/profile-context.js";

const MemeDetails = () => {
  const [meme, setMeme] = useState({});
  const [ourMeme, setOurMeme] = useState({});
  const [liked, setLiked] = useState();
  const [disliked, setDisliked] = useState();
  const { memeID } = useParams();
  const { checkLoggedIn } = useProfile();

  const fetchMemeByID = async () => {
    const response = await memeService.fetchMemesFromAPI();
    setMeme(response.data.data.memes.find((curMeme) => curMeme.id === memeID));
  };

  const fetchMemeByIDfromDatabase = async () => {
    const response = await memeService.fetchMemeById(memeID);
    setOurMeme(response.data);
  };

  const checkLiked = async () => {
    const curSessionUser = await checkLoggedIn();
    console.log(curSessionUser._id);
    const curUser = await userService.fetchUserById(curSessionUser._id);
    const curLiked = curUser.data.likedMemes.includes(memeID);
    setLiked(curLiked);
    const curDisliked = curUser.data.dislikedMemes.includes(memeID);
    setDisliked(curDisliked);
  };

  const handleMemeLikes = async () => {
    const curMeme = {
      name: meme.name,
      memeID: meme.id,
      url: meme.url,
    };
    const response = await memeService.likeMeme(curMeme);
    setOurMeme(response.data);
  };

  const handleUserLikes = async () => {
    const curUser = await checkLoggedIn();
    await userService.likeMeme(curUser, memeID);
  };

  const handleMemeUnlikes = async () => {
    const curMeme = {
      title: meme.title,
      memeID: meme.id,
      url: meme.url,
    };
    const response = await memeService.unlikeMeme(curMeme);
    setOurMeme(response.data);
  };

  const handleUserUnlikes = async () => {
    const curUser = await checkLoggedIn();
    await userService.unlikeMeme(curUser, memeID);
  };

  const handleMemeDislikes = async () => {
    const curMeme = {
      name: meme.name,
      memeID: meme.id,
      url: meme.url,
    };
    const response = await memeService.likeMeme(curMeme);
    setOurMeme(response.data);
  };

  const handleUserDislikes = async () => {
    const curUser = await checkLoggedIn();
    await userService.likeMeme(curUser, memeID);
  };

  const handleMemeUndislikes = async () => {
    const curMeme = {
      title: meme.title,
      memeID: meme.id,
      url: meme.url,
    };
    const response = await memeService.unlikeMeme(curMeme);
    setOurMeme(response.data);
  };

  const handleUserUndislikes = async () => {
    const curUser = await checkLoggedIn();
    await userService.unlikeMeme(curUser, memeID);
  };

  useEffect(() => {
    fetchMemeByID();
    fetchMemeByIDfromDatabase();
    checkLiked();
  }, []);

  return (
    <div>
      <h1>{meme.name}</h1>
      <img src={meme.url} height={500} />
      <SecureContent
        loggedInContent={
          <div>
            {" "}
            <i
              className={`${
                liked ? "fas fa-thumbs-up text-danger" : "fas fa-thumbs-up"
              }`}
              onClick={() => {
                if (liked) {
                  handleMemeUnlikes();
                  handleUserUnlikes();
                  setLiked(false);
                } else {
                  handleMemeLikes();
                  handleUserLikes();
                  setLiked(true);
                }
              }}
            ></i>
            {(ourMeme && ourMeme.likes) || 0}
            <i
              className={`${
                disliked
                  ? "fas fa-thumbs-fown text-primary"
                  : "fas fa-thumbs-down"
              }`}
            ></i>
          </div>
        }
        nonloggedInContent={<div>please login to like or comment</div>}
      />
    </div>
  );
};

export default MemeDetails;
