  //       (curMeme) => curMeme.id === memeid
  //     );
  //     if (curMeme) {
  //       return memeArr.push(curMeme);
  //     }
  //   });
  //   setLikedMemes(memeArr);
  // };

  // const fetchFavMeme = async () => {
  //   const response = await memeService.fetchMemesFromAPI();
  //   const curUser = await userService.fetchUserById(profile._id);
  //   const memeID = curUser.data.favoriteMeme;
  //   setFavMeme(
  //     response.data.data.memes.find((curMeme) => curMeme.id === memeID)
  //   );
  // };

  // const fetchCommentedMemes = async () => {
  //   const response = await commentService.findCommentsByUserId(profile._id);
  //   const updatedComments = [];
  //   response.map(async (curMeme) => {
  //     const curMemeFromApi = await memeService.fetchMemesFromAPI();
  //     const existing = curMemeFromApi.data.data.memes.find(
  //       (curMemeFromAPI) => curMemeFromAPI.id === curMeme.memeID
  //     );
  //     if (existing) {
  //       updatedComments.push(curMeme);
  //     }
  //   });
  //   console.log(response);
  //   setComments(updatedComments);
  // };
  const profileInfo = ProfileDisplay(profile._id);
  return user ? (
    <div className="wrapper">
      <h2>Profile: {user.data.username}</h2>

      <button className="btn btn-danger" onClick={logout}>
        Logout
      </button>
      <hr />
      <div className="left-text position-relative">
        <div className="left-text">
          Email
          <input
            placeholder="email"
            className="form-control"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          Username
          <input
            placeholder="username"
            className="form-control"
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          Password
          <input
            placeholder="password"
            className="form-control"
            type="text"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        {!isValid && (
          <div className="alert alert-danger alert-dismissible" role="alert">
            <strong>Holy guacamole!</strong> You should check in on some of
            those fields above.
          </div>
        )}
        <div className="d-flex">
          <button onClick={handleUpdate} className="btn btn-primary">
            Update
          </button>
        </div>
      </div>
      {profileInfo}
    </div>
  ) : (
    ""
  );
};

export default Profile;
