import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
import { Link, useLocation, useNavigate, useParams } from "react-router-dom";

const SearchOmdb = () => {
  const [movies, setMovies] = useState([]);
  const { searchString } = useParams();
  const titleRef = useRef();
  const navigate = useNavigate();
  const location = useLocation();
  const meme_URL = "https://api.imgflip.com/get_memes";
  const searchMoviesByTitle = async () => {
    const response = await axios.get(`${meme_URL}`);
    setMovies(response.data);
    // navigate(`/omdb/${titleRef.current.value}`);
  };
  // useEffect(() => {
  //   if (searchString) {
  //     titleRef.current.value = searchString;
  //     searchMoviesByTitle();
  //   }
  // }, []);
  return (
    <div>
      <h1>Search Omdb</h1>

      <ul className="list-group">
        <li className="list-group-item">
          <button
            onClick={searchMoviesByTitle}
            className="btn btn-primary float-end"
          >
            Search
          </button>
          <input ref={titleRef} className="form-control w-75" />
        </li>
        <p> ${movies} </p>
      </ul>
    </div>
  );
};

export default SearchOmdb;
