import { useEffect, useState } from "react";
import * as memeService from "../services/meme-service";

const Home = () => {
  const [topMeme, setTopMeme] = useState();

  const fetchTopMeme = async () => {
    const response = await memeService.fetchMemesFromAPI();
    const topMeme = response.data.data.memes[0];
    setTopMeme(topMeme);
  };

  useEffect(() => {
    fetchTopMeme();
  });
  return (
    <div>
      {topMeme && (
        <div className="center-text bg-light meme-detail">
          <h2>{topMeme.name}</h2>
          <div className={"meme-div"}>
            <img src={topMeme.url} className={"meme-image"} />
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
