import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
import { Link, useLocation, useNavigate, useParams } from "react-router-dom";

const MemesSearch = () => {
  const [memes, setMemes] = useState([]);
  const { searchString } = useParams();
  const titleRef = useRef();
  const navigate = useNavigate();
  const location = useLocation();
  const meme_URL = "https://api.imgflip.com/get_memes";
  const fetchMemes = async () => {
    const response = await axios.get(`${meme_URL}`);
    const search = titleRef.current.value || searchString;
    // if (search === "") {
    //   const first10 = response.data.data.memes
    //     .sort(() => Math.random() - 0.5)
    //     .slice(0, 10);
    //   setMemes(first10);
    // } else {
    setMemes(
      response.data.data.memes.filter((meme) =>
        meme.name.toLowerCase().includes(search)
      )
    );

    // navigate(`/omdb/${titleRef.current.value}`);
    // }
    titleRef.current.value = search;
    navigate(`/meme-search/${search}`);
  };

  useEffect(() => {
    fetchMemes();
  }, []);
  return (
    <div>
      <h1>Search Memes</h1>

      <ul className="list-group">
        <li className="list-group-item">
          <button onClick={fetchMemes} className="btn btn-primary float-end">
            Search
          </button>
          <input ref={titleRef} className="form-control w-75" />
        </li>
        {memes.map((meme) => (
          <li className="list-group-item">
            <Link to={`/`}>
              <img src={meme.url} height={30} className="me-2" />
              {meme.name}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MemesSearch;
