import React, { useRef, useEffect, useState } from "react";
import * as service from "../services/auth-service";
import { Link, useNavigate } from "react-router-dom";
import { useProfile } from "../contexts/profile-context.js";
import * as userService from "../services/user-service.js";
import * as memeService from "../services/meme-service.js";
const Profile = () => {
  const { profile } = useProfile();
  const navigate = useNavigate();
  const [user, setUser] = useState();
  const [email, setEmail] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isValid, setIsValid] = useState(true);

  const logout = async () => {
    await service.logout();
    navigate("login");
  };

  const checkUser = async () => {
    const curUser = await userService.fetchUserById(profile._id);
    setUser(curUser);
    setEmail(curUser.data.email);
    setUsername(curUser.data.username);
    setPassword(curUser.data.password);
  };

  const handleUpdate = async () => {
    if (!(email === "" || username === "" || password === "")) {
      try {
        await userService.updateUser(user, email, username, password);
        const curUser = await userService.fetchUserById(profile._id);
        setUser(curUser);
      } catch (e) {
        setIsValid(false);
      }
    } else {
      setIsValid(false);
    }
  };

  const fetchMemeByID = async (memeID) => {
    const response = await memeService.fetchMemesFromAPI();
    return response.data.data.memes.find((curMeme) => curMeme.id === memeID);
  };

  const renderMemes = () => {
    return user.data.likedMemes.map(async (memeid) => {
      const curMeme = await fetchMemeByID(memeid);
      return (
        <Link to={`details/${curMeme.id}`} className="list-group-item">
          <img src={curMeme.url} height={30} className="me-2 meme-search" />
          {curMeme.name}
        </Link>
      );
    });
  };

  useEffect(() => {
    checkUser();
  }, [setUser]);

  return user ? (
    <div>
      <h2>Profile: {user.data.username}</h2>

      <button className="btn btn-danger" onClick={logout}>
        Logout
      </button>
      <hr />
      <div className="left-text position-relative">
        <div className="left-text">
          Email
          <input
            placeholder="email"
            className="form-control"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          Username
          <input
            placeholder="username"
            className="form-control"
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          Password
          <input
            placeholder="password"
            className="form-control"
            type="text"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        {!isValid && (
          <div className="alert alert-danger alert-dismissible" role="alert">
            <strong>Holy guacamole!</strong> You should check in on some of
            those fields above.
          </div>
        )}
        <div className="d-flex">
          <button onClick={handleUpdate} className="btn btn-primary">
            Update
          </button>
        </div>
      </div>

      {user.data.likedMemes.length !== 0 && (
        <div>
          <hr />
          {renderMemes}
        </div>
      )}
    </div>
  ) : (
    ""
  );
};

export default Profile;
