import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const MemeDetails = () => {
  const [meme, setMeme] = useState({});
  const meme_URL = "https://api.imgflip.com/get_memes";
  const { memeID } = useParams();
  const fetchMemeByID = async () => {
    const response = await axios.get(`${meme_URL}`);
    setMeme(response.data.data.memes.find((curMeme) => curMeme.id === memeID));
  };

  useEffect(() => {
    fetchMemeByID();
    console.log(meme);
  }, []);

  return (
    <div>
      <h1>{meme.name}</h1>
      <img src={meme.url} height={500} />
    </div>
  );
};

export default MemeDetails;
