  //   navigate(`/meme-search/${search}`);
  // };

  useEffect(() => {
    fetchMemes();
  }, []);
  return (
    <div>
      <h1>Search Memes</h1>

      <ul className="list-group">
        <li className="list-group-item">
          <button onClick={fetchMemes} className="btn btn-primary float-end">
            Search
          </button>
          <input ref={titleRef} className="form-control w-75" />
        </li>
        {memes.map((meme) => (
          <li className="list-group-item">
            <Link to={`/`}>
              <img src={meme.url} height={30} className="me-2" />
              {meme.name}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MemesSearch;
