import React, { useEffect, useState } from "react";
import * as service from "../services/auth-service";
import { Link, useNavigate } from "react-router-dom";
import { useProfile } from "../contexts/profile-context.js";
import * as userService from "../services/user-service.js";

const Profile = () => {
  const { profile } = useProfile();
  const navigate = useNavigate();
  const [user, setUser] = useState();

  const logout = async () => {
    await service.logout();
    navigate("login");
  };

  const checkUser = async () => {
    const curUser = await userService.fetchUserById(profile._id);
    setUser(curUser);
    console.log(user);
  };
  useEffect(() => {
    checkUser();
  }, [setUser]);

  return (
    <div>
      <h1>Profile</h1>
      <button className="btn btn-danger" onClick={logout}>
        Logout
      </button>
      <hr />
      {user && user.data.username}
      <div className="left-text position-relative">
        <h2>Login</h2>
        <div className="left-text">
          Email
          <input
            ref={emailRef}
            placeholder="email"
            className="form-control"
            type="email"
          />
          Password
          <input
            ref={passwordRef}
            placeholder="password"
            className="form-control"
            type="password"
          />
        </div>
        {!isValid && (
          <div className="alert alert-danger alert-dismissible" role="alert">
            <strong>Holy guacamole!</strong> You should check in on some of
            those fields above.
          </div>
        )}
        <div className="d-flex">
          <button onClick={handleSignin} className="btn btn-primary">
            Signin
          </button>
          <div className="pt-2 ps-3">
            Dont have an account? <Link to={"/profile/signup"}>SignUp</Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
