import React, { useRef, useEffect, useState } from "react";
import * as service from "../services/auth-service";
import { Link, useNavigate } from "react-router-dom";
import { useProfile } from "../contexts/profile-context.js";
import * as userService from "../services/user-service.js";
import * as memeService from "../services/meme-service.js";
import * as commentService from "../services/comment-service.js";

const Profile = () => {
  const { profile } = useProfile();
  const navigate = useNavigate();
  const [user, setUser] = useState();
  const [email, setEmail] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isValid, setIsValid] = useState(true);
  const [likedMemes, setLikedMemes] = useState([]);
  const [favMeme, setFavMeme] = useState();
  const [comments, setComments] = useState([]);
  const [commentedMemes, setCommentedMemes] = useState([]);

  const logout = async () => {
    await service.logout();
    navigate("login");
  };

  const checkUser = async () => {
    const curUser = await userService.fetchUserById(profile._id);
    setUser(curUser);
    setEmail(curUser.data.email);
    setUsername(curUser.data.username);
    setPassword(curUser.data.password);
  };

  const handleUpdate = async () => {
    if (!(email === "" || username === "" || password === "")) {
      try {
        await userService.updateUser(user, email, username, password);
        const curUser = await userService.fetchUserById(profile._id);
        setUser(curUser);
      } catch (e) {
        setIsValid(false);
      }
    } else {
      setIsValid(false);
    }
  };

  const loadLikedMemes = async (memeID) => {
    const response = await memeService.fetchMemesFromAPI();
    const curUser = await userService.fetchUserById(profile._id);
    const memeArr = [];
    curUser.data.likedMemes.map(async (memeid) => {
      const curMeme = response.data.data.memes.find(
        (curMeme) => curMeme.id === memeid
      );
      return memeArr.push(curMeme);
    });
    setLikedMemes(memeArr);
  };

  const fetchFavMeme = async () => {
    const response = await memeService.fetchMemesFromAPI();
    const curUser = await userService.fetchUserById(profile._id);
    const memeID = curUser.data.favoriteMeme;
    setFavMeme(
      response.data.data.memes.find((curMeme) => curMeme.id === memeID)
    );
  };

  const fetchComments = async () => {
    const response = await commentService.findCommentsByUserId(profile._id);
    setComments(response);
    const commentedMemesArr = [];
    response.map(async (commentedMemes) => {
      console.log(commentedMemes);
      const curMeme = await memeService.fetchMemeById(commentedMemes.memeID);
      commentedMemesArr.push(curMeme);
    });
    console.log(commentedMemesArr);
    setCommentedMemes(commentedMemesArr);
  };

  const findMemeByID = async (id) => {
    const response = await memeService.fetchMemeById(id);
    return response;
  };
  let i = 0;
  useEffect(() => {
    checkUser();
    loadLikedMemes();
    fetchFavMeme();
    fetchComments();
  }, [setUser, setFavMeme, setComments]);

  return user ? (
    <div className="wrapper">
      <h2>Profile: {user.data.username}</h2>

      <button className="btn btn-danger" onClick={logout}>
        Logout
      </button>
      <hr />
      <div className="left-text position-relative">
        <div className="left-text">
          Email
          <input
            placeholder="email"
            className="form-control"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          Username
          <input
            placeholder="username"
            className="form-control"
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          Password
          <input
            placeholder="password"
            className="form-control"
            type="text"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        {!isValid && (
          <div className="alert alert-danger alert-dismissible" role="alert">
            <strong>Holy guacamole!</strong> You should check in on some of
            those fields above.
          </div>
        )}
        <div className="d-flex">
          <button onClick={handleUpdate} className="btn btn-primary">
            Update
          </button>
        </div>
      </div>
      {favMeme && (
        <div>
          <hr />
          <h5>Favorite Meme:</h5>
          <div className="meme-display">
            <Link
              to={`/meme-search/details/${favMeme.id}`}
              className="list-group-item"
            >
              <img
                src={favMeme.url}
                height={30}
                className="me-2 meme-image-display"
              />
              {favMeme.name}
            </Link>
          </div>
        </div>
      )}
      {user.data.likedMemes.length !== 0 && (
        <div>
          <hr />
          <h5>Liked Memes:</h5>
          <div className="meme-display liked-memes">
            {likedMemes.map((meme) => (
              <Link
                to={`/meme-search/details/${meme.id}`}
                className="list-group-item"
              >
                <img
                  src={meme.url}
                  height={30}
                  className="me-2 meme-image-display"
                />
                {meme.name}
              </Link>
            ))}
          </div>
        </div>
      )}
      {comments.length !== 0 && (
        <div>
          <hr />
          <h5>Comments:</h5>
          <div className="meme-display liked-memes">
            {comments.map((comment) => {
              <li className="list-group-item comment-item">
                {comment && comment.username} on{" "}
                <Link to={`/meme-search/details/${commentedMemes}`}>
                  {" "}
                  {findMemeByID().name}
                </Link>
                :<p className="comment">{comment && comment.comment}</p>
              </li>;
              i++;
            })}
          </div>
        </div>
      )}
    </div>
  ) : (
    ""
  );
};

export default Profile;
